import React, {Component} from 'react';
import './App.css';
import DropDownMenu from './DropDownMenu';
import News from './News/News';

// function App () {
//   return (
//     <div className ="App" >
//       <h1>Hey</h1>
//       <News/>
//     </div>
//   );

// }
class App extends Component{

  // const country = 'us';
  // const category = 'technology';

  state = {
    country : 'us',
    category : 'business',
    url : '',
  }

  handleCallBack = (countryCode) => {
    this.setState({
      country : countryCode
    })
  }
  // callBackCategory = (categoryKeyword) => {
  //   this.setState({
  //     category : categoryKeyword
  //   })
  // }
  countryChanged = (event) =>{
    let countryVar = this.state.country;
    let categoryVar = this.state.category;
    this.setState({
      country : event.target.value,
      url : 'https://newsapi.org/v2/top-headlines?country='+ countryVar +'&category='+categoryVar+'&apiKey=7b38a50dc5ac4437b56c2f2290b9d8d8',
    })

  }

  render() {

    return (
      <div >
        <div className="App-header">
          <p>Worldwide News </p>
        </div>

        <DropDownMenu chosenCountry = {this.handleCallBack} changeHandler= {this.countryChanged}/>
        {this.state.country}



        <div className ="App" >
          <News url = {this.state.url} />
        </div>
        </div>
    );
  }
}
export default App;
