import React, {Component} from 'react';
import './App.css';
import DropDownMenu from './DropDownMenu';
import News from './News/News';

// function App () {
//   return (
//     <div className ="App" >
//       <h1>Hey</h1>
//       <News/>
//     </div>
//   );

// }
class App extends Component{

  // const country = 'us';
  // const category = 'technology';

  state = {
    country : 'us',
    category : 'technology',
    url : 'https://newsapi.org/v2/top-headlines?country=us&category=business&apiKey=7b38a50dc5ac4437b56c2f2290b9d8d8',
    newsArray : [],
    // url : '',
  }

  handleCallBack = (countryCode) => {
    this.setState({
      country : countryCode
    })
  }
  // callBackCategory = (categoryKeyword) => {
  //   this.setState({
  //     category : categoryKeyword
  //   })
  // }
  countryChanged = (event) =>{
    let countryVar = event.target.value;
    let categoryVar = this.state.category;
    this.setState({
      country : event.target.value,
      url : 'https://newsapi.org/v2/top-headlines?country='+ countryVar +'&category='+categoryVar+'&apiKey=7b38a50dc5ac4437b56c2f2290b9d8d8',
    })
    fetch(this.state.url)
    .then((response) => {
        return response.json();
    })
    .then((data) => {
        // console.log(data);
        this.setState({
            newsArray : data.articles
            // totalNum : data.totalResults,
        })
    })
    .catch((error) => console.log(error));

  }

  render() {

    return (
      <div >
        <div className="App-header">
          <p>Worldwide News </p>
        </div>

        <DropDownMenu  changeHandler= {this.countryChanged}/>
        {this.state.country}



        <div className ="App" >
          <News news = {this.state.newsArray} />
        </div>
        </div>
    );
  }
}
export default App;
