import React, {Component} from 'react';
import NewSingle from './NewSingle.js';

class News extends Component {

    constructor(props) {
        super(props);
        this.state = {
            news : [],

            // totalNum : 0,
        };
    }

    componentDidMount() {
        const country = this.props.country;
        const category = this.props.category;
        const url = 'https://newsapi.org/v2/top-headlines?country='+country+'&category='+category+'&apiKey=7b38a50dc5ac4437b56c2f2290b9d8d8';

        fetch(url)
            .then((response) => {
                return response.json();
            })
            .then((data) => {
                // console.log(data);
                this.setState({
                    news : data.articles
                    // totalNum : data.totalResults,
                })
            })
            .catch((error) => console.log(error));
    }

    renderItems() {
        return this.state.news.map((item) =>(
            <NewSingle key={item.url} item = {item} />
        )) ;
    }

    render() {
        return (
            <div>
                {this.renderItems()}
            </div>
        );
    }
}
export default News;
