import React, {Component} from 'react';

class DropDownMenu extends Component{
    sendData = (event) => {
        this.props.chosenCategory(event.target.mycategory.value);
        // event.preventDefault();
        // this.props.chosenCategory('business');
    }

    render(){
        return(
            <div>
                <p>returning dropdown list </p>

                <input type="text" placeholder="Enter code" name="mycountry" onChange={this.props.changeHandler}></input>
                <input type="text" placeholder="Enter Category" name="mycategory" onChange={sendData}></input>
            </div>

        );
    }
}

export default DropDownMenu;
