import React, {Component} from 'react';
import './App.css';
import DropDownMenu from './DropDownMenu';
import News from './News/News';
import NewSingle from './News/NewSingle.js';

// function App () {
//   return (
//     <div className ="App" >
//       <h1>Hey</h1>
//       <News/>
//     </div>
//   );

// }


function Newss(props) {
  return (
  <div>
    Enter Zip Code : 
    <input type="text" onChange={ props.changeHandler } />
  </div>

  );
}

class App extends Component{

  // const country = 'us';
  // const category = 'technology';

  state = {
    country : '',
    category : 'technology',
    newsArray : [],
    // url : '',
  }

  handleCallBack = (countryCode) => {
    this.setState({
      country : countryCode
    })
  }
  // callBackCategory = (categoryKeyword) => {
  //   this.setState({
  //     category : categoryKeyword
  //   })
  // }
  countryChanged = (event) =>{
    this.setState({
      country : event.target.value,
    })
    let countryVar = event.target.value;
    let categoryVar = this.state.category;
    let url = 'https://newsapi.org/v2/top-headlines?country='+ countryVar +'&category='+categoryVar+'&apiKey=7b38a50dc5ac4437b56c2f2290b9d8d8';

    if (countryVar.length === 2){
      fetch(url)
      .then((response) => {
          return response.json();
      })
      .then(data => {
          console.log(data.articles);
          this.setState({
              newsArray : data.articles,
              // totalNum : data.totalResults,
          });
      })
      .catch((error) => console.log(error));

    }

  }

  render() {
    const {newsArray}  = this.state;

    return (
      <div >
        <div className="App-header">
          <p>Worldwide News </p>
        </div>

        <DropDownMenu  changeHandler= {this.countryChanged}/>
        {this.state.country}



        <div className ="App" >
          {/* <Newss newsArr = {this.state.newsArray} /> */}
          newsArray.map((item) =>(
            <NewSingle key={item.url} item = {item} />
          ))
        </div>
      </div>
    );
  }
}
export default App;
